mapper.select:
SELECT * FROM board_post
WHERE board_idx = #{boardIdx}
  AND delete_yn = 0 AND secret_yn=0
ORDER BY notice_yn DESC, idx DESC
    LIMIT #{offset}, #{pageSize}

WITH RECURSIVE Hierarchy AS ( -- 최상위 부모 글 선택 SELECT bp.*, hb.parent_idx, hb.depth, hb.sorts, bp.idx AS root_idx, CAST(bp.idx AS CHAR(255)) AS hierarchy_path FROM board_post bp LEFT JOIN hierarchical_board hb ON bp.idx = hb.post_idx WHERE bp.board_idx = #{boardIdx} AND bp.delete_yn = 0 AND hb.parent_idx IS NULL
UNION ALL
-- 부모 아래에 자식 글 추가 SELECT bp.*, hb.parent_idx, hb.depth, hb.sorts, h.root_idx, CONCAT(h.hierarchy_path, '-', LPAD(bp.idx, 10, '0')) AS hierarchy_path FROM board_post bp JOIN hierarchical_board hb ON bp.idx = hb.post_idx JOIN Hierarchy h ON hb.parent_idx = h.idx WHERE bp.board_idx = #{boardIdx} AND bp.delete_yn = 0 ) SELECT * FROM Hierarchy ORDER BY notice_yn DESC, -- 공지 글을 최상위로 정렬 root_idx DESC, -- 최상위 부모 글을 최신 순으로 정렬 hierarchy_path, sorts DESC LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE board_post
SET hits = hits+1
WHERE idx=#{idx}

UPDATE board_post
SET title = #{title}, content = #{content}, upload_date=#{uploadDate}, notice_yn=#{noticeYn}, secret_yn=#{secretYn}
WHERE idx = #{idx}

UPDATE board_post
SET delete_yn=1
WHERE idx = #{idx}
